  // }

  return (
    <Router>
      <main>
        <div className="App">
          <nav className="navigation" id="navbar">
            <h1 className="site-header">Erin Davis Front-End Web Developer</h1>
            <div className="nav-links">
            <Link to="/" className="links">Home</Link>
            <Link to="/about" className="links">About Me</Link>
            <Link to="/github" className="links" >Github</Link>
            <Link to="/linkedin" className="links">LinkedIn</Link>
            <Link to="/skills" className="links">Skills</Link>
            <Link to="/projects" className="links">Projects</Link>
            <Link to="/contactme" className="links">Contact Me</Link>
            </div>
          </nav>
          <div className="app-body">
          <LandingPage />
          <Projects />
          <SkillsPage />
          <About />
          <ContactForm />
        {/*  <Route exact path="/">
            <LandingPage />
          </Route>
          //commented out <SkillsPage />
          <Route path="projects">
            <Projects />
          </Route>
          <Route path="/about">
            <About />
          </Route>
          <Route path="/contactme">
            <ContactForm />
          </Route>

          <Route exact path="/">
            <LandingPage />
          </Route>
          <Route path="/skills">
            <SkillsPage />
          </Route>
          <Route path="">
          </Route>
          <Route path="">
          </Route>
          <Route path="">
          </Route>
          <Route path="">
          </Route>*/}
          </div>
        </div>
      </main>
    </Router>
  );
}

export default App;
