import React, {useState} from "react";
import "../styles/ContactForm.css";
import emailjs from "emailjs-com"

export default function ContactForm(){

  /*on submit should pop and alert saying it's been submitted or just change the
  contact form to just a message that says "I'll get back to you soon!"
  Also make sure all the inputs except the checkbox are required
  */
  const [emailValue, setEmailValue] = useState({
    name: "",
    email: "",
    message: ""
  })
  const handleChanges = e =>{
    setEmailValue({...emailValue, [e.target.name]: [e.target.value] });
  }

  const sendEmail = (e) =>{
    e.preventDefault();

    emailjs.sendForm('gmail', 'template_31u6koi', e.target, 'user_wAhuZV6QrAHl0yvShygvC')
      .then((result) => {
          console.log(result.text);
      }, (error) => {
          console.log(error.text);
      });
    e.target.reset()
  }

  return (
    <div className="contact comp">
      <h1>Contact Me!</h1>
      <form onSubmit={sendEmail}>
        <label for="name">Name:</label>
        <input
        type="text"
        id="name"
        name="name"
        onChange={handleChanges}
        placeholder="Your Name"
        />
        <label for="email">Email Address:</label>
        <input
        type="email"
        id="email"
        name="email"
        onChange={handleChanges}
        placeholder="Your Email Address"
        />
        <label for="message">Message:</label>
        <input
        type="textarea"
        id="message"
        name="message"
        placeholder="Your message here!"
        />
        <button type="submit">Submit</button>
      </form>
    </div>
  );
}
